package com.hm.qyhm.common.result;

public class Result {
	
	protected int code;
	
	protected String msg;
	
	public Result() {
		// TODO Auto-generated constructor stub
	}
	
	public Result(int code, String msg) {
		this.code = code;
		this.msg = msg;
	}

	public int getCode() {
		return code;
	}

	public void setCode(int code) {
		this.code = code;
	}

	public String getMsg() {
		return msg;
	}

	public void setMsg(String msg) {
		this.msg = msg;
	}
	
}
